import './AddItemForm.css';
import { useRef } from 'react';

function AddCategoryForm(props) {
  const categoryInputRef = useRef();
//   const priceInputRef = useRef();
//   const categoryInputRef = useRef();
  function formSubmitHandler(e) {
    e.preventDefault();
    const categoryValue = categoryInputRef.current.value;

    const category = {
      categoryName: categoryValue
    }
    props.onAddItem(category);
  }

  return (
    <form onSubmit={formSubmitHandler}>
      <label>Kategooria nimi</label><br />
      <input type="text" placeholder="Kategooria nimi" required ref={categoryInputRef} /><br />
      {/* <label>Eseme hind</label><br />
      <input type="number" required ref={priceInputRef} /><br />
      <label>Eseme kategooria</label><br />
      <input type="text" required ref={categoryInputRef} /><br /> */}
      <button>Sisesta uus kategooria</button>
    </form>
  );
}

export default AddCategoryForm;
